import {
  Drawer,
  IconButton,
  InputAdornment,
  TextField,
  Button,
  Grid,
  Typography,
  Divider,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import "../../style/LeftDrawer.css";
import { useState } from "react";
import setting from "../../assets/icons/settings.png";
import signout from "../../assets/icons/signout.png";
import SearchIcon from "@mui/icons-material/Search";

interface LeftDrawerProps {
  open: boolean;
  onClose: (data: boolean) => void;
}

export default function LeftDrawer({ open, onClose }: LeftDrawerProps) {
  const [searchText, setSearchText] = useState<string>("");

  return (
    <Drawer
      anchor="left"
      open={open}
      variant="persistent"
      classes={{
        paper: "custom-drawer-paper",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        <strong style={{ fontSize: "1.2rem" }}>Menu</strong>
        <IconButton onClick={() => onClose(false)}>
          <MenuIcon />
        </IconButton>
      </div>

      <TextField
        size="small"
        color="secondary"
        value={searchText}
        placeholder="Search"
        onChange={(e) => setSearchText(e.target.value)}
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <SearchIcon />
            </InputAdornment>
          ),
        }}
      />

      <div>
        <Typography variant="body2" sx={{ fontWeight: "bold" }}>
          TASK
        </Typography>

        <Divider />
      </div>

      <div>
        <Typography variant="body2" sx={{ fontWeight: "bold" }}>
          LIST
        </Typography>

        <Divider />
      </div>
      <div>
        <Typography variant="body2" sx={{ fontWeight: "bold" }}>
          TAGS
        </Typography>

        <Divider />
      </div>

      <Grid
        container
        spacing={2}
        style={{ position: "absolute", bottom: "5%", left: "0" }}
      >
        <Grid item xl={6}>
          <Button onClick={() => alert("open setting")} color="secondary">
            <img
              src={setting}
              alt="setting-icon"
              width="15%"
              style={{ marginRight: "15px" }}
            />
            Setting
          </Button>
        </Grid>
        <Grid item xl={6}>
          <Button onClick={() => alert("sign out")} color="secondary">
            <img
              src={signout}
              alt="logout-icon"
              width="15%"
              style={{ marginRight: "15px" }}
            />
            Sign Out
          </Button>
        </Grid>
      </Grid>
    </Drawer>
  );
}
