        <Grid container spacing={4} style={{ padding: '20px' }}>
            <Grid item xs={6}>
                <div style={{ backgroundColor: 'black', borderRadius: '2%', border: 'solidj1px #cdcdcd', height: '90vh', padding: '20px' }}>
