import { FormControl, InputLabel, OutlinedInput, TextField } from '@mui/material';
import { ChangeEvent, useState } from 'react';

interface loginSchema {
    email: string,
    password: string
}

export default function Signin() {

    const [userCred, setUserCred] = useState<loginSchema>({email: '', password: ''});
    const [showPassword, setShowPassword] = useState<boolean>(false);

    const handleEmailOnChange = (e: ChangeEvent<HTMLInputElement>) => {
        setUserCred({...userCred, email: e.target.value})
    }
    return (
        <div>
            <h1>Sign In</h1>
            <TextField
                variant='outlined'
                label="Email"
                value={userCred.email}
                size='small'
                onChange={handleEmailOnChange} />

            <FormControl size='small' variant='outlined'>
                <InputLabel>Password</InputLabel>
                <OutlinedInput
                    type={showPassword ? 'text' : 'password'}
                    endAdornment />
            </FormControl>
        </div>
    )
}
