import { Visibility, VisibilityOff } from '@mui/icons-material';
import { Button, FormControl, Grid, IconButton, InputAdornment, InputLabel, OutlinedInput, TextField } from '@mui/material';
import { ChangeEvent, useState, FC } from 'react';
import HorizontalLineWithText from '../HorizontalLineWithText/HorizontalLineWithText';
import './Auth.css';

interface loginSchema {
    email: string,
    password: string
}

type ChildProps = {
    onButtonClick: (data :boolean) => void;
}

export default function Signin({onButtonClick}:ChildProps) {

    const [userCred, setUserCred] = useState<loginSchema>({email: '', password: ''});
    const [showPassword, setShowPassword] = useState<boolean>(false);

    const handleEmailOnChange = (e: ChangeEvent<HTMLInputElement>) => {
        setUserCred({...userCred, email: e.target.value})
    }

    const handleSignIn = () => {
        alert('handle Signin with AWS Cognito');
    }

    const handleGoogleLogin = () => {
        alert('Google login');
    }

    const handleFacebookLogin = () => {
        alert('Facebook Login');
    }

    return (
        <div className='SignInComponent'>
            <h1>Sign In</h1>
            <TextField
                variant='outlined'
                label="Email"
                value={userCred.email}
                size='small'
                onChange={handleEmailOnChange} />

            <FormControl size='small' variant='outlined'>
                <InputLabel>Password</InputLabel>
                <OutlinedInput
                    type={showPassword ? 'text' : 'password'}
                    endAdornment={
                        <InputAdornment position='end'>
                            <IconButton
                                aria-label='toggle password visibility'
                                onClick={() => setShowPassword(!showPassword)}
                                edge='end'>
                                {showPassword ? <VisibilityOff /> : <Visibility />}
                            </IconButton>
                        </InputAdornment>
                    } />
            </FormControl>

            <Button onClick={handleSignIn} variant='contained'>Sign In</Button>
            <HorizontalLineWithText text='or' />
            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <Button onClick={handleGoogleLogin} sx={{ width: '100%' }} variant='contained' color='secondary'>Google</Button>
                </Grid>
                <Grid item xs={6}>
                    <Button onClick={handleFacebookLogin} sx={{ width: '100%' }} variant='contained' color='secondary'>Facebook</Button>
                </Grid>
            </Grid>

            <Button onClick={() => onButtonClick('signup')}>Don't have an account? Sign Up</Button>

        </div>
    )
}
