import {
  Drawer,
  IconButton,
  InputAdornment,
  TextField,
  Button,
  List,
  Typography,
  Divider,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import "../../style/LeftDrawer.css";
import { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import DoubleArrowIcon from "@mui/icons-material/DoubleArrow";
import {
  CalendarMonth,
  FormatListBulleted,
  Logout,
  TextSnippet,
  Tune,
} from "@mui/icons-material";

interface LeftDrawerProps {
  open: boolean;
  onClose: (data: boolean) => void;
}

export default function LeftDrawer({ open, onClose }: LeftDrawerProps) {
  const [searchText, setSearchText] = useState<string>("");

  const taskItems = [
    {
      title: "Upcoming",
      icon: <DoubleArrowIcon />,
    },
    {
      title: "Today",
      icon: <FormatListBulleted />,
    },
    {
      title: "Calendar",
      icon: <CalendarMonth />,
    },
    {
      title: "Sticky Wall",
      icon: <TextSnippet />,
    },
  ];

  return (
    <Drawer
      anchor="left"
      open={open}
      variant="persistent"
      classes={{
        paper: "custom-drawer-paper",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          marginBottom: "20px",
        }}
      >
        <Typography variant="h5">Menu</Typography>
        <IconButton onClick={() => onClose(false)}>
          <MenuIcon />
        </IconButton>
      </div>

      <TextField
        size="small"
        color="secondary"
        value={searchText}
        placeholder="Search"
        sx={{ marginBottom: "20px" }}
        onChange={(e) => setSearchText(e.target.value)}
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <SearchIcon />
            </InputAdornment>
          ),
        }}
      />

      <div>
        <Typography variant="body2" sx={{ fontWeight: "bold" }}>
          TASK
        </Typography>

        <List>
          {taskItems.map((item, idx) => (
            <ListItem disablePadding key={idx}>
              <ListItemIcon>{item.icon}</ListItemIcon>
              <ListItemText primary={item.title} />
            </ListItem>
          ))}
        </List>

        <Divider />
      </div>

      <div>
        <Typography variant="body2" sx={{ fontWeight: "bold" }}>
          LIST
        </Typography>

        <Divider />
      </div>
      <div>
        <Typography variant="body2" sx={{ fontWeight: "bold" }}>
          TAGS
        </Typography>

        <Divider />
      </div>

      <div>
        <Button onClick={() => alert("open setting")} color="secondary">
          <Tune />
          Setting
        </Button>
        <Button onClick={() => alert("sign out")} color="secondary">
          <Logout />
          Sign Out
        </Button>
      </div>
    </Drawer>
  );
}
